import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import './ImageDropzone.css';

const ImageDropzone = () => {
  const [images, setImages] = useState([]);
  const [previewUrls, setPreviewUrls] = useState([]);
  const [processedImages, setProcessedImages] = useState({});
  const [processing, setProcessing] = useState(false);
  const [error, setError] = useState(null);

  const onDrop = useCallback((acceptedFiles) => {
    // Update images state
    setImages(prevImages => [...prevImages, ...acceptedFiles]);

    // Create preview URLs for the images
    const newPreviewUrls = acceptedFiles.map(file => URL.createObjectURL(file));
    setPreviewUrls(prevUrls => [...prevUrls, ...newPreviewUrls]);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif']
    }
  });

  const processImage = async (file, index) => {
    try {
      setProcessing(true);
      setError(null);

      const formData = new FormData();
      formData.append('image', file);

      const response = await axios.post('http://localhost:5000/api/process-image', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.success) {
        setProcessedImages(prev => ({
          ...prev,
          [index]: {
            image: `data:image/jpeg;base64,${response.data.processed_image}`,
            yolo_detections: response.data.yolo_detections,
            e_waste_detection: response.data.e_waste_detection
          }
        }));
      } else {
        throw new Error('Processing failed');
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setProcessing(false);
    }
  };

  const removeImage = (index) => {
    setImages(prevImages => prevImages.filter((_, i) => i !== index));
    setPreviewUrls(prevUrls => {
      const newUrls = [...prevUrls];
      URL.revokeObjectURL(newUrls[index]);
      return newUrls.filter((_, i) => i !== index);
    });
    setProcessedImages(prev => {
      const newProcessed = { ...prev };
      delete newProcessed[index];
      return newProcessed;
    });
  };

  return (
    <div className="image-dropzone-container">
      <div {...getRootProps()} className={`dropzone ${isDragActive ? 'active' : ''}`}>
        <input {...getInputProps()} />
        {isDragActive ? (
          <p>Drop the images here...</p>
        ) : (
          <p>Drag and drop images here, or click to select files</p>
        )}
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="preview-container">
        {previewUrls.map((url, index) => (
          <div key={index} className="preview-card">
            <div className="preview-image-container">
              <img 
                src={processedImages[index]?.image || url} 
                alt={`preview ${index}`} 
                className="preview-image"
              />
              <div className="preview-controls">
                <button 
                  className="process-button"
                  onClick={() => processImage(images[index], index)}
                  disabled={processing}
                >
                  {processing ? 'Processing...' : 'Process'}
                </button>
                <button 
                  className="remove-button"
                  onClick={() => removeImage(index)}
                >
                  Ã—
                </button>
              </div>
            </div>
            {processedImages[index] && (
              <div className="detections-box">
                <div className="detection-section">
                  <h4>Waste Classification:</h4>
                  {processedImages[index].yolo_detections.map((det, i) => (
                    <div key={i} className="detection-item">
                      <span className="detection-class">
                        {det.class_name.charAt(0).toUpperCase() + det.class_name.slice(1)}
                      </span>
                      <span className="detection-confidence">
                        Confidence: {(det.confidence * 100).toFixed(1)}%
                      </span>
                    </div>
                  ))}
                </div>
                {processedImages[index].e_waste_detection && (
                  <div className="detection-section">
                    <h4>E-Waste Classification:</h4>
                    <div className="detection-item">
                      <span className="detection-class">
                        {processedImages[index].e_waste_detection.class_name
                          .split('_')
                          .map(word => word.charAt(0).toUpperCase() + word.slice(1))
                          .join(' ')}
                      </span>
                      <span className="detection-confidence">
                        Confidence: {(processedImages[index].e_waste_detection.confidence * 100).toFixed(1)}%
                      </span>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ImageDropzone;
