import React, { useCallback, useState, useEffect } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import './ImageDropzone.css';

const API_BASE_URL = 'http://localhost:5000';
const API_TIMEOUT = 30000;

const ImageDropzone = () => {
  const [image, setImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [processedImage, setProcessedImage] = useState(null);
  const [processing, setProcessing] = useState(false);
  const [error, setError] = useState(null);
  const [retryCount, setRetryCount] = useState(0);
  const [backendStatus, setBackendStatus] = useState('checking');
  const MAX_RETRIES = 3;
  const [aiAnalysis, setAiAnalysis] = useState(null);

  // Configure axios instance
  const api = axios.create({
    baseURL: API_BASE_URL,
    timeout: API_TIMEOUT,
    headers: {
      'Content-Type': 'application/json',
    }
  });

  // Check backend connection
  const checkBackendConnection = async () => {
    try {
      const response = await api.get('/api/test');
      if (response.data.status === 'connected') {
        setBackendStatus('connected');
        setError(null);
        return true;
      }
      throw new Error('Backend not ready');
    } catch (err) {
      setBackendStatus('disconnected');
      setError('Cannot connect to backend service. Please ensure the server is running.');
      return false;
    }
  };

  // Check backend health
  const checkBackendHealth = async () => {
    try {
      const response = await api.get('/api/health');
      const status = response.data;

      if (status.status === 'healthy') {
        setBackendStatus('healthy');
        setError(null);
      } else if (status.status === 'degraded') {
        setBackendStatus('degraded');
        setError(`Backend service is degraded: ${status.message.join(', ')}`);
      } else {
        setBackendStatus('unhealthy');
        setError(`Backend service is unhealthy: ${status.message.join(', ')}`);
      }
    } catch (err) {
      setBackendStatus('error');
      setError('Error checking backend health. Please try again later.');
    }
  };

  // Initial connection and health check
  useEffect(() => {
    const initializeBackend = async () => {
      const isConnected = await checkBackendConnection();
      if (isConnected) {
        await checkBackendHealth();
      }
    };

    initializeBackend();
    // Set up periodic health checks
    const healthCheckInterval = setInterval(checkBackendHealth, 30000); // Check every 30 seconds

    return () => clearInterval(healthCheckInterval);
  }, []);

  const onDrop = useCallback((acceptedFiles) => {
    if (acceptedFiles.length > 0) {
      const file = acceptedFiles[0];
      setImage(file);
      setPreviewUrl(URL.createObjectURL(file));
      // Reset previous results
      setProcessedImage(null);
      setAiAnalysis(null);
    }
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.gif']
    },
    maxFiles: 1
  });

  const processImage = async (isRetry = false) => {
    // First check if backend is available
    if (backendStatus === 'disconnected') {
      const isConnected = await checkBackendConnection();
      if (!isConnected) {
        return;
      }
    }

    try {
      if (!isRetry) {
        setRetryCount(0);
      }
      setProcessing(true);
      setError(null);

      const formData = new FormData();
      formData.append('image', image);

      const response = await api.post('/api/process-image', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.success) {
        setProcessedImage({
          image: `data:image/jpeg;base64,${response.data.processed_image}`,
          detections: response.data.detections
        });
        setAiAnalysis(response.data.ai_analysis);
      } else {
        throw new Error(response.data.error || 'Processing failed');
      }
    } catch (err) {
      const errorMessage = err.response?.data?.error || err.message;

      if (err.code === 'ECONNABORTED' || err.message.includes('Network Error')) {
        await checkBackendConnection();
        if (retryCount < MAX_RETRIES) {
          setError(`Connection error. Retrying... (Attempt ${retryCount + 1}/${MAX_RETRIES})`);
          setRetryCount(prev => prev + 1);
          setTimeout(() => processImage(true), 2000);
          return;
        }
      }

      setError(`Error: ${errorMessage}. ${retryCount >= MAX_RETRIES ? 'Max retries reached.' : ''}`);
    } finally {
      if (retryCount >= MAX_RETRIES || !error) {
        setProcessing(false);
      }
    }
  };

  const removeImage = () => {
    setImage(null);
    setPreviewUrl(null);
    setProcessedImage(null);
    setAiAnalysis(null);
    if (previewUrl) {
      URL.revokeObjectURL(previewUrl);
    }
  };

  const groupDetectionsByModel = (detections) => {
    return detections.reduce((acc, det) => {
      if (!acc[det.model]) {
        acc[det.model] = [];
      }
      acc[det.model].push(det);
      return acc;
    }, {});
  };

  return (
    <div className="image-dropzone-container">
      {backendStatus !== 'healthy' && backendStatus !== 'connected' && (
        <div className={`status-message ${backendStatus}`}>
          <span>{error || 'Checking backend status...'}</span>
          <button 
            className="retry-button"
            onClick={checkBackendConnection}
            disabled={processing}
          >
            Check Connection
          </button>
        </div>
      )}
      {!image ? (
        <div {...getRootProps()} className={`dropzone ${isDragActive ? 'active' : ''}`}>
          <input {...getInputProps()} />
          {isDragActive ? (
            <p>Drop the image here...</p>
          ) : (
            <p>Drag and drop an image here, or click to select a file</p>
          )}
        </div>
      ) : (
        <div className="preview-card">
          <div className="preview-content">
            <div className="left-section">
              <div className="preview-image-container">
                <img 
                  src={processedImage?.image || previewUrl} 
                  alt="preview" 
                  className="preview-image"
                />
                <div className="preview-controls">
                  <button 
                    className="process-button"
                    onClick={processImage}
                    disabled={processing}
                  >
                    {processing ? 'Processing...' : 'Process'}
                  </button>
                  <button 
                    className="remove-button"
                    onClick={removeImage}
                  >
                    Ã—
                  </button>
                </div>
              </div>
              {processedImage?.detections && (
                <div className="detections-box">
                  {Object.entries(groupDetectionsByModel(processedImage.detections)).map(([model, detections]) => (
                    <div key={model} className="model-results">
                      <h4>{model === 'waste' ? 'Waste Detection' : 
                           model === 'medwaste' ? 'Medical Waste Detection' : 
                           'Battery Detection'}</h4>
                      {detections.map((det, i) => (
                        <div key={i} className="detection-item">
                          <span className="detection-class">
                            {det.class_name.charAt(0).toUpperCase() + det.class_name.slice(1)}
                          </span>
                          <span className="detection-confidence">
                            Confidence: {(det.confidence * 100).toFixed(1)}%
                          </span>
                        </div>
                      ))}
                    </div>
                  ))}
                </div>
              )}
            </div>
            {processedImage?.detections && aiAnalysis?.success && (
              <div className="ai-analysis-box">
                <h4>AI Analysis</h4>
                <div className="ai-analysis-content">
                  {aiAnalysis.analysis
                    .split('\n')
                    .filter(line => line.trim() && !line.match(/^\d+\./))
                    .map((line, i) => (
                      <p key={i}>{line}</p>
                    ))}
                </div>
                {aiAnalysis.previous_analyses && aiAnalysis.previous_analyses.length > 0 && (
                  <div className="historical-analysis">
                    <h4>Historical Analysis</h4>
                    {aiAnalysis.previous_analyses.map((analysis, index) => (
                      <div key={index} className="historical-entry">
                        <div className="historical-timestamp">
                          {new Date(analysis.timestamp).toLocaleString()}
                        </div>
                        <div className="historical-content">
                          {analysis.analysis
                            .split('\n')
                            .filter(line => line.trim() && !line.match(/^\d+\./))
                            .map((line, i) => (
                              <p key={i}>{line}</p>
                            ))}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      )}
      {error && (
        <div className="error-message">
          {error}
          {error.includes('Network error') && (
            <button 
              className="retry-button"
              onClick={() => processImage(true)}
              disabled={processing}
            >
              Retry Now
            </button>
          )}
        </div>
      )}
    </div>
  );
};

export default ImageDropzone;
